export class Client {
    baseURL: string;
    apiURL: string;

    player: Player;
    constructor(readonly options: ClientOptions);

    // Private methods
    private valiDateOptions(): void;

    // Public methods
    public rest<T = any>(method: string, url: string): Promise<T>;
}

export class Player {
    constructor(readonly client: Client);

    // Public methods
    public async find(options?: PlayerFindOptions): Promise<PlayerData[]>;

    // Private methods
    private valiDateData(data: PlayerRawData): PlayerData;
    private valiDateFindOptions(
        options: PlayerFindOptions,
        path: string
    ): string;
}

